mapper.select:
SELECT q.qna_idx ,q.qna_title ,q.qna_show ,q.qna_date ,m.mem_id,q.qna_status
FROM qna q  left join member m on q.mem_idx = m.mem_idx  	

	where qna_show = #{param1}

order by qna_idx desc
LIMIT #{param2},#{param3}

SELECT q.qna_idx ,q.qna_title ,q.qna_show ,q.qna_date ,m.mem_id,q.qna_status,q.qna_pass
FROM qna q  left join member m on q.mem_idx = m.mem_idx  	
WHERE qna_status = 'A' 

	 ANd qna_show = #{param1} 

order by qna_idx desc
LIMIT #{param2},#{param3}
