mapper.select:
SELECT mem_id
FROM member
WHERE mem_name = #{param1} AND mem_number = #{param2}

mapper.update:
UPDATE member 
 SET mem_pw = #{param1}
 WHERE mem_id = #{param2} AND mem_number=#{param3}

mapper.select:
SELECT COUNT(*) FROM member WHERE 1=1
AND mem_status = #{searchType}
AND mem_id LIKE CONCAT('%', #{keyword}, '%')
